mapper.select:
select  d.id,u.user_nickname nickName,u.user_email userEmail,d.push_content pushContent,d.push_time pushTime,d.push_image pushImage,d.zan_num zanNum,d.forward_id forwardId,d.type dtype,d.forward_num forwardNum
from dynamics d,user u
where u.id = d.user_id and u.id=#{userId}

select d.id did,u.user_nickname duserNickname,u.user_email duserEmail,d.push_time dpushTime, d.push_content dpushContent, d.push_image dpushImage
from dynamics d,user u
where d.user_id = u.id and d.id = #{forwardId}

mapper.update:
update dynamics
set zan_num = #{zanNum}
where id = #{dynamicId}

mapper.select:
update dynamics
set forward_num=#{forwardNum}
where dynamics.id=#{dynamicId}
