mapper.insert:
/* insert into dynamics(user_id, push_time, push_content, push_image) values(#{userId},#{pushTime},#{pushContent},#{pushImg})*/
insert into dynamics(user_id, push_time, push_content, forward_id, type) values(#{userId},#{pushTime},#{pushContent}, #{forwardId}, #{type})
insert into dynamics(user_id, push_time, push_content, push_image) values(#{userId},#{pushTime},#{pushContent},#{pushImg})

mapper.select:
select  d.id,u.user_nickname nickName,u.user_email userEmail,d.push_content pushContent,d.push_time pushTime,d.push_image pushImage,d.zan_num zanNum,d.forward_id forwardId
from dynamics d,user u
where u.id = d.user_id and u.id=#{userId}

mapper.update:
update dynamics
set zan_num = #{zanNum}
where id = #{dynamicId}
