mapper.select:
select  distinct d.id,u.user_nickname nickName,u.user_email userEmail, d.push_time pushTime, d.push_content pushContent, d.push_image pushImage
from dynamics d,collect c,user u
where d.id = c.dynamic_id and  u.id = d.user_id and c.user_id = #{userId}

select count(*)
from comment
where dynamics_id = #{dynamicId}

select count(*)
from collect
where dynamic_id = #{dynamicId}

mapper.delete:
       delete from collect
       where user_id=#{userId}  and dynamic_id in

			#{dynamicId}
